	(3, 'Bangalore-Based Defence & Aerospace Startup Becomes The First Private Company To Develop A Supersonic Ramjet Engine In India', 'https://startuppedia.in/startup-stories/bangalore-based-defence-aerospace-startup-becomes-the-first-private-company-to-develop-a-supersonic-ramjet-engine-in-india-8722699', 'Startuppedia', 7, 84, '["India", "defense", "startup", "ramjet engine", "artillery", "Hyprix", "aerospace"]', 5, '2025-02-22 22:42:37.078967+00', '{"people": [{"name": "Devmalya Biswas", "role": "Co-founder & CEO", "company": "Hyprix"}, {"name": "Divyanshu Mandowara", "role": "Co-founder & COO", "company": "Hyprix"}], "companies": [{"name": "Hyprix", "role": "developer"}, {"name": "DRDO", "role": "research_organization"}, {"name": "Boeing", "role": "competitor"}, {"name": "Raytheon", "role": "competitor"}, {"name": "Nammo", "role": "competitor"}], "facilities": [{"name": "NIT Surat", "type": "education"}, {"name": "Indian Army Artillery Directorate", "type": "military"}], "technologies": [{"name": "Tezz", "type": "ramjet_engine", "status": "prototype"}, {"name": "Kira M1", "type": "artillery_shell", "status": "development"}, {"name": "AI-guided precision guidance", "status": "development"}]}', '{"timeline": {"phase": "early_success", "milestones": {"successful_test": "January 2025", "company_founding": "June 2024", "engine_development_start": "August 2024"}, "development_time": "5 months"}, "market_data": {"wartime_demand": "1.8 million shells/year", "current_production": "300,000 shells/year", "defense_production_target_2029": "3 lakh crore INR"}, "narrative_thread": {"type": "breakthrough_innovation", "themes": ["domestic defense capability", "startup disruption", "young innovators", "strategic autonomy"], "stakeholders": ["Indian Army", "Defense Ministry", "Private Defense Sector"]}, "performance_metrics": {"speed_range": "Mach 2-4", "artillery_range": "4x traditional range", "efficiency_improvement": "3-4x over conventional systems"}}', 78, '2025-02-15', 'Indi