	(4, 'Turning the Moon into a fuel depot will take a lot of power', 'https://arstechnica.com/science/2025/02/turning-the-moon-into-a-fuel-depot-will-take-a-lot-of-power/', 'Ars Technica', 6, 67, '["Moon", "space infrastructure", "fuel production", "lunar resources", "energy", "SpaceX", "NASA"]', 5, '2025-02-22 22:44:09.035448+00', '{"companies": [{"name": "SpaceX", "role": "spacecraft_provider"}, {"name": "NASA", "role": "research_agency"}], "resources": [{"name": "ilmenite", "formula": "FeTiO3", "location": "lunar regolith"}, {"name": "liquid oxygen", "type": "fuel_component"}, {"name": "hydrogen", "type": "catalyst_and_fuel"}], "facilities": [{"name": "International Space Station", "type": "space_station", "power_capacity": "100 kW"}, {"name": "Earth-Moon Lagrange Point 1", "type": "orbital_point"}, {"name": "Lunar Mare regions", "type": "resource_location"}], "technologies": [{"name": "ilmenite processing", "status": "prototype", "efficiency": "24kWh/kg oxygen"}, {"name": "Starship", "type": "spacecraft", "oxygen_capacity": "500 tonnes"}, {"name": "solar arrays", "status": "operational"}, {"name": "nuclear power", "status": "proposed"}]}', '{"timeline": {"phase": "research", "key_metrics": {"moon_to_lagrange": "4kg fuel per 1kg payload", "earth_to_lagrange": "25kg fuel per 1kg payload", "oxygen_production_rate": "4kg per hour at 100kW", "empty_starship_requirement": "80 tonnes oxygen"}, "current_date": "2025-02-17"}, "challenges": ["massive power requirements", "continuous operation needs", "infrastructure transport costs", "resource extraction efficiency"], "narrative_thread": {"type": "technical_analysis", "importance": "critical_infrastructure", "impact_areas": ["solar system exploration", "space infrastructure", "resource utilization"]}, "technical_details": {"energy_distribution": {"water_splitting": "38%", "hydrogen_reaction": "55%", "oxygen_liquefaction": "5%"}, "production_timeline": {"one_tonne": "10 days", "empty_starship_fuel": "2 years"}}}', 65, '2025-02-17', 'Lunar Gas Stations Need Nuclear Power: Engineers Face Massive Energy Challenge ⚡', 'lunar-fuel-depot-power-requirements', 'Converting lunar resources into fuel faces significant energy challenges. Processing moon regolith into oxygen requires massive power infrastructure – about 24kWh per kg of oxygen. With current technology, fueling an empty Starship would take approximately 2 years, highlighting the need for nuclear power solutions on the Moon.', '{"title": null, "keywords": [], "image_url": null, "description": null, "article_type": "NewsArticle", "canonical_url": null}'),
	(5, 'Concern about SpaceX influence at NASA grows with new appointee', 'https://arstechnica.com/space/2025/02/as-nasa-flies-into-turbulence-the-agency-could-use-a-steady-hand/', 'Ars Technica', 8, 91, '["NASA", "SpaceX", "policy", "Trump administration", "Artemis", "ISS", "leadership"]', 5, '2025-02-22 22:50:41.522048+00', '{"people": [{"name": "Janet Petro", "role": "Interim Administrator", "company": "NASA"}, {"name": "Michael Altenhofen", "role": "Senior Advisor", "company": "NASA", "previous": "SpaceX"}, {"name": "Jared Isaacman", "role": "Administrator Nominee", "company": "NASA"}, {"name": "Elon Musk", "role": "Founder", "company": "SpaceX"}], "programs": [{"name": "Artemis", "status": "under review"}, {"name": "Mars Sample Return", "status": "pending decision"}, {"name": "Lunar Gateway", "status": "uncertain"}, {"name": "International Space Station", "status": "end planned 2030"}, {"name": "Polaris Program", "status": "active"}], "companies": [{"name": "SpaceX", "role": "contractor"}, {"name": "NASA", "role": "agency"}]}', '{"timeline": {"phase": "transition", "key_events": {"altenhofen_hire": "February 2025", "isaacman_hearing": "Expected mid-February 2025", "petro_appointment": "January 2025"}, "current_date": "2025-02-04"}, "critical_issues": [{"topic": "Mars Sample Return", "impact": "high", "status": "likely cancellation"}, {"topic": "Artemis Program", "impact": "critical", "status": "major changes contemplated"}, {"topic": "ISS Future", "impact": "high", "status": "underfunded transition"}], "narrative_thread": {"type": "agency_transformation", "themes": ["SpaceX influence", "policy uncertainty", "commercial transition", "leadership changes"], "stakeholder_concerns": ["morale issues", "budget uncertainty", "program continuity", "conflict of interest"]}}', 88, '2025-02-04', 'NASA Crisis: SpaceX Executive Joins as Trump Shakes Up Space Agency 🚨', 'nasa-spacex-leadership-concerns', 'NASA faces leadership turmoil as former SpaceX executive Michael Altenhofen joins as Senior Advisor, while Jared Isaacman awaits confirmation as Administrator nominee. Key programs including Mars Sample Return, Artemis, and ISS transition face uncertain futures, with concerns about SpaceX''s growing influence over agency direction and policy.', '{"title": null, "keywords": [], "image_url": null, "description": null, "article_type": "NewsArticle", "canonical_url": null}'),
	(6, 'Russia''s plasma engine could reach Mars in 30 days, drastically reduce space travel time', 'https://interestingengineering.com/space/russia-plasma-engine-could-reach-mars-early', 'Interesting Engineering', 5, 59, '["plasma propulsion", "Mars", "Russia", "Rosatom", "space travel", "electromagnetic propulsion"]', 5, '2025-02-22 22:48:58.660142+00', '{"people": [{"name": "Egor Biriulin", "role": "Junior Researcher", "company": "Rosatom"}, {"name": "Alexei Voronov", "role": "First Deputy General Director", "company": "Troitsk Institute"}, {"name": "Konstantin Gutorov", "role": "Scientific Adviser", "company": "Rosatom"}], "companies": [{"name": "Rosatom", "role": "developer"}, {"name": "Troitsk Institute", "role": "research_facility"}], "technologies": [{"name": "plasma electric rocket engine", "type": "propulsion", "specs": {"power": "300 kW", "thrust": "6N", "particle_speed": "100 km/s", "operational_time": "2400 hours"}}, {"name": "test chamber", "specs": {"length": "14 meters", "diameter": "4 meters"}}]}', '{"timeline": {"phase": "prototype_testing", "milestones": {"lab_prototype": "completed", "ground_testing": "ongoing"}, "current_date": "2025-02-10", "flight_model_target": "2030"}, "narrative_thread": {"type": "breakthrough_announcement", "verification_status": "pending peer review", "competing_technologies": ["water propulsion (Italy)", "lightsails"]}, "technical_details": {"mechanism": "electromagnetic acceleration", "advantages": ["no temperature overload", "efficient energy conversion", "highest thrust in class"], "comparison": {"plasma_engine": "100 km/s", "traditional_engines": "4.5 km/s"}, "propellant": "hydrogen"}}', 75, '2025-02-10', 'Moscow Claims New Engine Could Reach Mars in Just 30 Days 🚀', 'russia-plasma-engine-mars-travel', 'Russian scientists claim their plasma electric rocket engine prototype could reduce Mars travel time to just 30 days. Developed by Rosatom, the engine generates 6N of thrust with particle speeds reaching 100 km/s – over