	(9, 'Hubble Discovers "Blue Lurker" Spinning So Fast, It''s Breaking Astronomers'' Models', 'https://scitechdaily.com/hubble-discovers-blue-lurker-spinning-so-fast-its-breaking-astronomers-models/', 'SciTechDaily', 6, 37, '["astronomy", "Hubble", "stellar evolution", "blue lurker", "star clusters", "M67"]', 5, '2025-02-22 22:56:50.091651+00', '{"people": [{"name": "Emily Leiner", "role": "Researcher", "institution": "Illinois Institute of Technology"}], "facilities": [{"name": "Hubble Space Telescope", "type": "space_telescope"}, {"name": "Kepler Space Telescope", "type": "space_telescope", "status": "retired"}], "organizations": [{"name": "NASA", "role": "research_agency"}, {"name": "ESA", "role": "research_agency"}, {"name": "Space Telescope Science Institute", "role": "operations"}, {"name": "Illinois Institute of Technology", "role": "research_institution"}], "celestial_objects": [{"name": "Blue Lurker", "type": "star", "distance": "2,800 light-years", "location": "M67 cluster", "properties": {"companion": "white dwarf", "rotation_period": "4 days", "white_dwarf_mass": "0.72 solar masses", "white_dwarf_temperature": "23,000 degrees Fahrenheit"}}]}', '{"timeline": {"merger_event": "~500 million years ago", "presentation": "245th American Astronomical Society meeting", "discovery_date": "2025-01"}, "narrative_thread": {"type": "scientific_discovery", "themes": ["stellar evolution", "observational astronomy", "unexpected phenomena"]}, "scientific_significance": {"implications": ["stellar evolution models", "triple star system dynamics", "mass transfer processes"], "unique_features": ["triple-star system evolution", "unusual rotation speed", "massive white dwarf companion", "rare observable case"]}}', 72, '2025-01-24', 'Hubble Finds Star Spinning So Fast It Defies Physics 🌟', 'hubble-blue-lurker-stellar-discovery', 'Hubble has discovered a "Blue Lurker" star that spins so rapidly it defies current stellar models. Located 2,800 light-years away in the M67 cluster, the star likely formed when a white dwarf merged with a companion star 500 million years ago, creating this rare observable case of triple-star system evolution.', '{"title": null, "keywords": [], "image_url": null, "description": null, "article_type": "NewsArticle", "canonical_url": null}'),
	(10, 'Connecting the Dots | Growth amid uncertainty', 'https://spacenews.com/connecting-the-dots-growth-amid-uncertainty/', 'SpaceNews', 6, 71, '["space economy", "market analysis", "SpaceX", "investment", "satellite", "launch industry"]', 5, '2025-02-22 22:51:57.046479+00', '{"people": [{"name": "Carissa Christensen", "role": "CEO", "company": "BryceTech"}, {"name": "Chris Quilty", "role": "CEO", "company": "Quilty Space"}, {"name": "Mark Boggett", "role": "CEO", "company": "Seraphim"}], "projects": [{"name": "Project Kuiper", "status": "development", "company": "Amazon"}, {"name": "IRIS²", "type": "sovereign broadband", "region": "Europe"}, {"name": "Starship", "status": "development", "company": "SpaceX"}], "companies": [{"name": "Novaspace", "role": "research_firm"}, {"name": "BryceTech", "role": "analytics_firm"}, {"name": "SpaceX", "role": "launch_provider"}, {"name": "Amazon", "role": "satellite_operator", "project": "Kuiper"}, {"name": "Quilty Space", "role": "research_firm"}, {"name": "Seraphim", "role": "venture_capital"}]}', '{"market_data": {"2024_total": "596 billion USD", "growth_rate": "7%", "2028_forecast": "944 billion USD", "2025_projected": "620 billion USD", "spacex_launches": {"2023": 96, "2024": 134, "2025_target": 180}, "direct_to_device": {"2024_users": "24 million", "2032_projection": "130 million monthly users", "revenue_milestone": "billion-dollar industry by 2027"}}, "growth_drivers": ["government support", "defense initiatives", "commercial partnerships", "venture capital interest", "sovereign wealth funds"], "emerging_sectors": ["direct-to-device satellite", "mineral exploration", "in-space manufacturing"], "narrative_thread": {"type": "market_analysis", "themes": ["industry expansion", "government catalyst", "commercial innovation", "investment trends"], "challenges": ["inflation", "US tariffs", "international competition"]}}', 82, '2025-01-29', 'Space Economy to Hit $620B in 2025 as Investment Soars 📈', 'space-economy-growth-620bn-2025-market-analysis', 'The global space economy is projected to reach $620 billion in 2025, growing 7% from $596 billion in 2024. Key growth drivers include government initiatives, defense spending, and venture capital interest. SpaceX plans 180 launches in 2025, while emerging sectors like direct-to-device satellite services are expected to become billion-dollar industries by 2027.', '{"title": null, "keywords": [], "image_url": null, "description": null, "article_type": "NewsArticle", "canonical_url": null}'),
	(8, 'A private start-up called Helion aims to have a working fusion reactor by 2028', 'https://www.engadget.com/science/a-private-start-up-called-helion-aims-to-have-a-working-fusion-reactor-by-2028-142020697.html', 'Engadget', 3, 88, '["fusion", "energy", "Helion", "startup", "clean energy", "nuclear", "Sam Altman"]', 5, '2025-02-22 22:55:06.065585+00', '{"people": [{"name": "David Kirtley", "role": "CEO", "company": "Helion"}, {"name": "Sam Altman", "role": "investor"}, {"name": "Peter Thiel", "role": "investor"}], "companies": [{"name": "Helion", "role": "developer", "founded": "2013", "valuation": "5.4 billion USD"}, {"name": "ITER", "role": "competitor", "type": "international_project"}], "competitors": [{"cost": "22 billion USD", "name": "ITER", "status": "under construction", "timeline": "2034+"}, {"name": "EAST reactor", "record": "1,066 seconds fusion", "location": "China"}], "technologies": [{"name": "Polaris reactor", "type": "magnetic fusion", "specs": {"fuel": ["deuterium", "helium-3"], "plasma_speed": "1 million MPH", "target_temperature": "100 million Celsius"}, "status": "in operation", "generation": "seventh"}]}', '{"timeline": {"phase": "development", "target_date": "2028", "current_date": "2025-01-28", "total_raised": "1 billion+ USD", "funding_round": "425 million USD"}, "market_impact": {"differentiators": ["private sector approach", "faster timeline", "novel technology"], "potential_applications": ["city-scale power generation", "carbon-free energy", "clean nuclear"]}, "narrative_thread": {"type": "breakthrough_attempt", "themes": ["private vs public development", "energy innovation", "ambitious timeline", "high-profile backing"]}, "technical_innovation": {"approach": "direct electricity generation", "advantages": ["no steam turbine", "simpler system", "potentially more efficient", "hourglass design"], "challenges": ["scaling up pulse rates", "controlling energy surges", "commercial viability"]}}', 85, '2025-01-28', 'Sam Altman-Backed Startup 